    public int maximumGap(final List<Integer> A) {
        //List<Integer> A1 = A.stream().peek((o1)->o1=5).collect(Collectors.toList());
        List<Integer> A_min = new ArrayList<>();
        List<Integer> A_max = new ArrayList<>();
        A_min.add(0);
        for(int i=1; i<A.size(); i++)
        {
            if( A.get(i) <= A.get(A_min.get(A_min.size()-1)) ) A_min.add(i);
            //A_min.add( A.get(i)>A_min.get(i-1)?A_min.get(i-1):A.get(i) );
        }
        A_max.add(A.size()-1);
        for(int i=A.size()-2; i>=0; i--)
        {
            if( A.get(i) >=  A.get(A_max.get(A_max.size()-1)) ) A_max.add(i);
            //A_max.add( A.get(i)<A_max.get(A_max.size()-1)?A_max.get(A_max.size()-1):A.get(i) );
        }
        int i=A_max.size()-1, j=0, delta=0;
        while(i>=0 && j<=A_min.size()-1)
        {
            if(A.get(A_max.get(i)) >= A.get(A_min.get(j)))
            {
                if( delta <= (A_max.get(i) - A_min.get(j)) ) {
                    delta = (A_max.get(i) - A_min.get(j));
                    //System.out.println(delta +"     "+ A_max.get(i)+"     "+ A_min.get(j)+"     "+ A.get(A_min.get(j))+"     "+ A.get(A_max.get(i)));
                    /*if(i>0*/ //i--;

                    //System.out.println(delta +"     "+ A_max.get(i)+"     "+ A_min.get(j));
                }
                 System.out.println(delta +"     "+ A_max.get(i)+"     "+ A_min.get(j)+"     "+ A.get(A_min.get(j))+"     "+ A.get(A_max.get(i))); i--;
            }
            else { System.out.println(delta +"     "+ A_max.get(i)+"     "+ A_min.get(j)+"     "+ A.get(A_min.get(j))+"     "+ A.get(A_max.get(i))); j++; }
        }
        return delta;
    }
